    public  void cargarDatosUsuario(){
    
        
        txtUser.setText(ControladorPrincipal.user.getUsuario());
        txtApellido.setText(ControladorPrincipal.user.getApellido());
        txtNombre.setText(ControladorPrincipal.user.getNombre());
       
    
    }
    public void cargarTablaCliente(int id,String dni){
    
     jTableCliente.setModel( ControladorPrincipal.listarCliente(id,dni));
    }
